import React, { useState } from 'react';

function Intro() {
    const shortestlIntro = 'Hi my name is Ruth. But I prefer Alanna.';
    const mediumIntro = `Hi my name is Ruth, But I prefer Alanna. I'm a web developer.`;
    const longIntro = `I am long intro`;
    const LongestIntro = `I am longest intro`;

    const [intro, setIntro] = useState(shortestlIntro); 
  return (
    <div className="intro-container">
        <div className="left-intro">
            <p>Alanna</p>
        </div>
        <div className="right-intro">
            <button onClick={()=> setIntro(intro)}>click</button>
            <button onClick={()=> setIntro(mediumIntro)}>click</button>
            <button onClick={()=> setIntro(longIntro)}>click</button>
            <button onClick={()=> setIntro(LongestIntro)}>click</button>
            <p>{intro}</p>
        </div>
    </div>
  );
}

export default Intro;
