import React from 'react';
import '../styles/projects.css';
import  { Link } from 'react-router-dom';

//images
import chow from '../images/the-chow.jpg';

const Projects = () => {

    const project = [

        {
            id: 1,
            title: 'project one',
            image: chow,
            cta: 'View project',
            link: '/project-one'
        },
        {
            id: 2,
            title: 'project two',
            image: chow,
            cta: 'View project',
            link: '/project-two'
        },
        {
            id: 3,
            title: 'project three',
            image: chow,
            cta: 'View project',
            link: '/project-three'
        }
    ]
    return (
        <section className='projects__container'>
            <p>Recent Work</p>
            {project.map(item => (

                <div className='project__card' key={item.id}>
                    <img src={item.image} alt={item.title} className='project__image'/>
                    <p className='project-title'>{item.title}</p>
                    <Link to={item.link}><button>{item.cta}</button></Link>
                </div>

            ))}
        </section>
    )
}

export default Projects;
