      <img 
        src={image}
        alt={props.title} 
        onMouseOver={() => 
          {setInfo(props.desc)}
        }
        onMouseLeave={() => 
          {setInfo()}
        }
        />
      <img 
        src={newImage}
        alt={props.title} 
        onMouseOver={() => 
          {setInfo(props.desc)}
        }
        onMouseLeave={() => 
          {setInfo()}
        }
      />
      <h1 className="project-info">{info}</h1>
    </div>
  );
}

export default Projects;
