import React from 'react';

//project images
import lavabeans from '../images/lavabeans.png';
import projectTwo from '../images/drawing3.svg';
import projectThree from '../images/drawing.svg';

//style={{backgroundImage:`url(${project.image})`, display: 'flex'}}

function Works() {
  const projectsData = [
    {
      id: 1,
      title: 'Lavabeans Coffee Company',
      image: lavabeans,
      description: 'This is description for project 1',
      hoverImage: 'actual project image one'
    },
    {
      id: 2,
      title: 'The Chow',
      image: lavabeans,
      description: 'This is description for project 2',
      hoverImage: 'actual project image two'
    },
    {
      id: 3,
      title: 'Mayday',
      image: lavabeans,
      description: 'This is description for project 3',
      hoverImage: 'actual project image three'
    },
    {
      id: 2,
      title: 'project 4',
      image: lavabeans,
      description: 'This is description for project 2',
      hoverImage: 'actual project image two'
    }
  ]

  return (
    <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around', backgroundC}}>
      {projectsData.map(project => (
        <div style={{display: 'flex'}}>
          <img style={{width: '600px'}}src={project.image} alt={project.title}/>
          <div>
            <h1>{project.title}</h1>
            <p>{project.description}</p>
          </div>
        </div>
      ))}
    </div>

  );
}

export default Works;
