import React, { useState } from 'react';

function Projects(props) {

  const [info, setInfo] = useState();

  return (
    <div className="projects-container">
      <img src={props.image} onClick={()=>{setInfo(<h1>{props.desc}</h1>)}}/>
      <h1>Hello</h1>
    </div>
  );
}

export default Projects;
