import React from "react";
import styled from 'styled-components';
import breakpoint from '../breakpoints';


const skills_one = [
  {
    title: 'JavaScript ES6+'
  },
  {
    title: 'Python'
  },
  {
    title: 'React'
  }
]

const skills_two = [
  {
    title: 'TypeScript'
  },
  {
    title: 'Tailwind CSS'
  },
  {
    title: 'HTML/CSS'
  }
]

const skills_three = [
  {
    title: 'GIT'
  },
  {
    title: 'Azure DevOps'
  },
  {
    title: 'Figma'
  }
]
const About = () => {
    return (
      <Container>
        <div className="wrapper">
          <div className="flex-container">
            <div className="info">
              <p>I'm a Frontend Developer at WebstaurantStore, where I work to improve the 
                experience of yacht vacationing and the booking of exotic vacations on the modern web.
                Currently, Iâ€™ve been honing my skills learning the backend as I navigate through Lambda School's 18-month 
                intensive Fullstack Development program. Through my journey, I've come to find that the more I learn, the 
                more there is left to know. Outside of work however, I spend most of my time reading, writing and catching 
                up with Game of Thrones.</p>
            </div>
            <div>
              <h3>Tech Stack</h3>
              <div className="skills">
                <div>
                  {skills_one.map(skill => (
                    <div>
                      <p>{skill.title}</p>
                    </div>
                  ))}
                </div>
                <div className="skills_two">
                  {skills_two.map(skill => (
                    <div>
                      <p>{skill.title}</p>
                    </div>
                  ))}
                </div>
                <div className="skills_two">
                  {skills_three.map(skill => (
                    <div>
                      <p>{skill.title}</p>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div> 
        </div>
      </Container>
    );
}

export default About;

const Container = styled.section`
  background: yellow;

  .wrapper {
    max-width: 1280px;
    overflow-x: hidden;
    margin: 0 auto;
    padding: 0;
    height: 35rem;
    background: yellow;
    display: grid;
    place-items: center;
  }

  .flex-container {
    width: 100%;
  }

  .info, .skills {
    color: #737373;
    font-size: 14px;
    line-height: 2rem;
    letter-spacing: 0.045rem;
  }

  h3 {
    color: #544667;
    text-align: center;
    text-transform: uppercase;
    font-size: 2rem;
    font-weight: 600;
    text-align: center;
    padding-bottom: 2rem;
  }

  .skills {
    display: flex;
    //padding-left: 10rem;
  }

  .skills_two {
    padding-left: 5rem;
  }

  @media only screen and ${breakpoint.device.mobile} {
    background: red;

    .wrapper {
      max-width: 1280px;
      margin: 0 auto;
      //height: 35rem;
      background: red;
      display: grid;
      place-items: center;
    }
  }

  @media only screen and ${breakpoint.device.tablet} {
    background: green;

    .wrapper {
      max-width: 1280px;
      margin: 0 auto;
      //height: 35rem;
      background: green;
      display: grid;
      place-items: center;
    }
  }

  @media only screen and ${breakpoint.device.desktop} {
    background: blue;

    .wrapper {
      max-width: 1280px;
      margin: 0 auto;
      //height: 35rem;
      background: blue;
      display: grid;
      place-items: center;
    }
  }
`
