import React, { useState, useEffect } from "react";

const Menu = () => {
//   const [activeMenu, setActiveMenu] = useState("about");
  const [scrollPositions, setScrollPositions] = useState({
    about: false,
    experience: false,
    projects: false,
  });

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const aboutElement = document.getElementById("about");
      const experienceElement = document.getElementById("experience");
      const projectsElement = document.getElementById("projects");

      if (aboutElement && experienceElement && projectsElement) {
        const aboutPosition = aboutElement.offsetTop;
        const experiencePosition = experienceElement.offsetTop;
        const projectsPosition = projectsElement.offsetTop;

        setScrollPositions({
          about: scrollTop >= aboutPosition - 300,
          experience: scrollTop >= experiencePosition - 300,
          projects: scrollTop >= projectsPosition - 300,
        });
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="pt-20" style={{ color: "#6687CC" }}>
      <a
        href="#about"
        className={scrollPositions.about ? "mb-6 block underline" : "mb-6 block"}
      >
        About
      </a>
      <a
        href="#experience"
        className={scrollPositions.experience ? "mb-6 block underline decoration-white" : "mb-6 block"}
      >
        Experience
      </a>
      <a
        href="#projects"
        className={scrollPositions.projects ? "block underline" : "block"}
      >
        Projects
      </a>
    </div>
  );
};

export default Menu;
