import React from 'react';

//project images
import lavabeans from '../images/lavabeans.png';
import projectTwo from '../images/drawing3.svg';
import projectThree from '../images/drawing.svg';

//style={{backgroundImage:`url(${project.image})`, display: 'flex'}}

function Works() {
  const projectsData = [
    {
      id: 1,
      title: 'Lavabeans Coffee Company',
      image: lavabeans,
      description: 'Business Bank Account Dashboard using React, Python and Django. This dashboard uses Authentication and Companies House API. Please make sure to register to view its full capacity, or view screenshots on my GitHub ReadMe file here',
      hoverImage: 'actual project image one'
    },
    {
      id: 2,
      title: '',
      image: lavabeans,
      description: 'This is description for project 2',
      hoverImage: 'actual project image two'
    },
    {
      id: 3,
      title: 'Mayday',
      image: lavabeans,
      description: 'This is description for project 3',
      hoverImage: 'actual project image three'
    },
    {
      id: 2,
      title: 'project 4',
      image: lavabeans,
      description: 'This is description for project 2',
      hoverImage: 'actual project image two'
    }
  ]

  return (
    <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around', backgroundColor: '#88BBBC'}}>
      {projectsData.map(project => (
        <div style={{display: 'flex', flexWrap: 'wrap'}}>
          <img style={{width: '550px', marginTop: '60px'}} src={project.image} alt={project.title}/>
          <div style={{width: '550px', marginTop: '60px', backgroundColor: 'yellow'}}>
            <h1>{project.title}</h1>
            <p>{project.description}</p>
          </div>
        </div>
      ))}
    </div>

  );
}

export default Works;
