import React from 'react';
import ImageGallery from 'react-image-gallery';
import Projects from './Project';

//project images
import lavabeans from '../images/lavabeans.png';
import thechow from '../images/the-chow.jpg';
import fittrackr from '../images/fit-trackr.png';

//style={{backgroundImage:`url(${project.image})`, display: 'flex'}}

function Works() {
  const projectsData = [
    {
      id: 1,
      title: 'Lavabeans Coffee Company',
      image: lavabeans,
      description: 'Business Bank Account Dashboard using React, Python and Django. This dashboard uses Authentication and Companies House API. Please make sure to register to view its full capacity, or view screenshots on my GitHub ReadMe file here.',
      tech: 'React, Redux, CSS, HTML5',
      category: 'Web Application',
      link: '',
      github: ''
    },
    {
      id: 4,
      title: 'The Chow',
      image: thechow,
      description: 'An app that allows user to rate burgers as well as nominate their favorite. The dashboard uses Authentication and a geolocation API, as well as Brewdogs open API. Please make sure to ',
      tech: 'JavaScript, HTML5, CSS',
      category: 'Static website',
      link: 'https://www.chowdining.com/',
      github: 'https://github.com/ruthmatieu/ruthmatieu.github.io'
    },
    {
      id: 2,
      title: 'Fit Trackr',
      image: fittrackr,
      description: 'A Vanilla JavaScript clone of the infamous Doodle Jump game.',
      tech: 'React, Redux, Firebase, Node, Express, HTML5, CSS',
      category: 'Game',
      link: 'https://fit-trackr.vercel.app/',
      github: 'https://github.com/ruthmatieu/fit-trackr/tree/master/fit-trackr'
    },
    // {
    //   id: 5,
    //   title: 'FitTrakr',
    //   image: lavabeans,
    //   description: 'A Vanilla JavaScript clone of the infamous Doodle Jump game.',
    //   tech: 'JavaScript, HTML5, CSS',
    //   category: 'Game',
    //   link: 'https://doodle-jump.vercel.app/',
    //   github: 'https://github.com/ruthmatieu/doodle-jump'
    // },
    // {
    //   id: 3,
    //   title: 'Jouveaux',
    //   image: lavabeans,
    //   description: 'A Vanilla JavaScript clone of the infamous Doodle Jump game.',
    //   tech: 'JavaScript, HTML5, CSS',
    //   category: 'E-commerce',
    //   link: 'https://doodle-jump.vercel.app/',
    //   github: 'https://github.com/ruthmatieu/doodle-jump'
    // },
  ]


  return (
    <div style={{ backgroundColor: 'white'}}>
      <h5>About Me</h5>
      <p style={{textAlign: 'left'}}>Hi there! I’m Josh. I’m a passionate introvert who loves building things with
        code.<br/>
        My first experience with programming was when I was 12, but I didn’t last
        long: I was a big fan of computer-animated TV shows like Reboot, and I
        decided I wanted to do “computer graphics”. My mom bought me a C++
        Reference Manual. It was several hundred pages, and totally inscrutable to me.
        Years later, I decided to try learning Python. It was a radically different
        experience. I distinctly remember the moment it clicked; I was watching The
        Price is Right, and they were spinning the big wheel. A contestant landed on
        0.70, and it made me wonder: statistically, what was their best move?
        It occurred to me: I had the skills to build a simulation! So I threw together a
        Python script that would run thousands of simulations to work out what the
        right answer was. 15 minutes later, I had the answer. My burgeoning skillset
        was a superpower: I could derive answers that were previously off-limits to me.
        It was magic.<br/>
        Since then, I’ve been honing my skills and learning Javascript. I recently
        graduated from Apex College’s 12-week intensive Web Development Bootcamp
        program. I’m seeking a full-time role where I can help a company achieve their
        goals.
      </p>
      {projectsData.map(project => (
        <div id={project.id}>

          <div style={{display: 'flex', flexWrap: 'wrap', justifyContent:'space-around', backgroundColor: 'red', margin: '10px 0'}}>
            <img 
              style={{width: '400px',height: '210px', marginTop: '60px'}} 
              src={project.image} 
              alt={project.title}

            />
            <Projects
                title={project.title}
                description={project.description}
                tech={project.tech}
                category={project.category}
                link={project.link}
                github={project.github}
              />
            </div>

        </div>
      ))}
    </div>

  );
}

export default Works;
