import React from "react";
import styled from 'styled-components';

const skills_left = [
  {
    title: 'JavaScript ES6+'
  },
  {
    title: 'Python'
  },
  {
    title: 'React'
  },
  {
    title: 'TypeScript'
  },
  {
    title: 'Tailwind CSS'
  }
]

const skills_right = [
  {
    title: 'HTML/CSS'
  },
  {
    title: 'GIT'
  },
  {
    title: 'Azure DevOps'
  },
  {
    title: 'Figma'
  },
  {
    title: 'JavaScript ES6+'
  }
]
const About = () => {
    return (
      <Container>
        <div className="wrapper">
          <div className="flex-container">
            <div className="info">
              <p>I'm a developer at Travelopia, where I work to improve the 
                experience of yacht vacationing and the booking of exotic vacations on the modern web.
                Currently, Iâ€™ve been honing my skills learning the backend as I navigate through Lambda School's 18-month 
                intensive Fullstack Development program. Through my journey, I've come to find that the more I learn, the 
                more there is left to know. Outside of work however, I spend most of my time reading, writing and catching 
                up with Game of Thrones.</p>
            </div>
            <div className="skills">
              <div>
                {skills_left.map(skill => (
                  <div>
                    <p>{skill.title}</p>
                  </div>
                ))}
              </div>
              <div>
                {skills_right.map(skill => (
                  <div>
                    <p>{skill.title}</p>
                  </div>
                ))}
              </div>
            </div>
          </div> 
        </div>
      </Container>
    );
}

export default About;

const Container = styled.section`
  background: white;

  .wrapper {
    max-width: 1280px;
    margin: 0 auto;
    height: 900px;
    background: white;
  }

  .flex-container {
    padding-top: 10rem;
    width: 100%;
    display: flex;
    justify-content: space-around;
    margin: 0 auto;
  }

  .info, .skills {
    width: 50%;
    line-height: 2rem;
    letter-spacing: 0.5rem;
  }

  .skills {
    display: flex;
    padding-left: 10rem;
  }
`
