import React, { useState } from 'react';

function Projects(props) {

  const [info, setInfo] = useState();

  return (
    <div className="projects-container">
      <img src={props.image} onHover={()=>{setInfo(props.desc)}}/>
      <h1>{info}</h1>
    </div>
  );
}

export default Projects;
